var MailData = function(data) {

    this.validate = function() {
        //mail sender
        if (data.from == undefined)
            throw new Error('Mail sender must be setted');

        //mail recipient
        if (data.to == undefined)
            throw new Error('Mail recipient must be setted');
        if (typeof data.to == "string")
            data.to = [data.to];

        //mail subject
        if (data.subject == undefined)
            throw new Error('Mail subject must me setted');

        //mail body
        if (data.body == undefined)
            data.body = '';
    };

    this.prepareFormat = function() {
        //TODO
    };

    this.getPreparedMailParams = function() {
        return {
            from:       data.from,
            to:         data.to,
            subject:    data.subject,
            html:       data.body
        };
    };


};

module.exports.init = function(data) {
    return new MailData( data );
};