var transportData = function(data) {

    this.validate = function() {
        if (data.host == undefined) {
            throw new Error('Remote transport host not setted');
        }
    };

    this.prepareFormat = function() {
        //TODO
    };

    this.getHost = function() {
        return data.host;
    };

    this.getAuthParams = function() {
        if (
            data.username == undefined ||
            data.password == undefined
        ) {
            throw new Error('User login and password required but not setted');
        }
        return {
            user: data.username,
            pass: data.password
        };
    }

};

module.exports.init = function(data) {
    return new transportData( data );
};